import React, { useState } from "react";
import About from "./About";
import Portfolio from "./pages/Portfolio";
import Contact from "./Contact";
import Resume from "./Resume";

export default function Navigation() {
  let [about, setAbout] = useState(<About />);
  let [portfolio, setPortfolio] = useState(<Portfolio />);
  let [contact, setContact] = useState(<Contact />);
  let [resume, setResume] = useState(<Resume />);

  const aboutClick = () => {
    setAbout(about);
  };

  const portfolioClick = () => {
    setPortfolio(portfolio);
  };

  const contactClick = () => {
    setContact(contact);
  };

  const resumeClick = () => {
    setResume(resume);
  };
  return (
    <nav>
      <ul className="nav">
        <li>
          <button type="button" onClick={aboutClick}>
            About Me
          </button>
        </li>
        <li>
          <a href="#portfolio" onClick={portfolioClick}>
            Portfolio
          </a>
        </li>
        <li>
          <a href="#contact" onClick={contactClick}>
            Contact
          </a>
        </li>
        <li>
          <a href="#resume" onClick={resumeClick}>
            Resume
          </a>
        </li>
      </ul>
    </nav>
  );
}
